INSERT INTO nko (name, description, logo, address, city_id, coords, meta, created_at) VALUES
('Благотворительный фонд «Подари жизнь»', 'Помощь детям с онкологическими и гематологическими заболеваниями. Фонд собирает средства на лечение, закупает медицинское оборудование и лекарства.', 'nko-logo/8476b1d0b0c67a4f6fe0afc152851068.jpeg', 'г. Москва, ул. Доватора, д. 13', 2, POINT(37.5665, 55.7522), '{"url": "https://podari-zhizn.ru"}', '2024-01-20 12:00:00+03'),
('Благотворительный фонд «Русфонд»', 'Адресная помощь тяжелобольным детям и взрослым. Работает в Москве и 16 регионах России, развивает Национальный регистр доноров костного мозга.', 'nko-logo/8476b1d0b0c67a4f6fe0afc152851068.jpeg', 'г. Москва, ул. Петровка, д. 26, стр. 2', 2, POINT(37.6156, 55.7658), '{"url": "https://rusfond.ru"}', '2024-01-20 12:00:00+03'),
('Благотворительный фонд помощи хосписам «Вера»', 'Поддержка московских и региональных хосписов, помощь людям с тяжелыми заболеваниями, обучение медицинского персонала паллиативной помощи.', 'nko-logo/8476b1d0b0c67a4f6fe0afc152851068.jpeg', 'г. Москва, ул. Трубная, д. 12', 2, POINT(37.6214, 55.7658), '{"url": "https://vera.ru"}', '2024-01-20 12:00:00+03'),
('Благотворительный фонд «Старость в радость»', 'Помощь одиноким пожилым людям в домах престарелых и интернатах. Более 20 000 волонтеров организуют мероприятия и поездки для пожилых людей.', 'nko-logo/8476b1d0b0c67a4f6fe0afc152851068.jpeg', 'г. Москва, ул. Маросейка, д. 9/2', 2, POINT(37.6389, 55.7578), '{"url": "https://starikam.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Волонтеры в помощь детям-сиротам»', 'Поддержка детей в сиротских учреждениях, помощь кровным и приемным семьям, работа над изменением законодательства в области защиты детей.', 'nko-logo/8476b1d0b0c67a4f6fe0afc152851068.jpeg', 'г. Москва, Лучников пер., д. 2', 2, POINT(37.6342, 55.7611), '{"url": "https://otkazniki.ru"}', '2024-01-20 12:00:00+03'),
('Благотворительный фонд «Второе дыхание»', 'Крупнейшая НКО по сбору, сортировке и переработке одежды. Продвигает идею осознанного потребления и сокращения объема мусора.', 'https://secondbreath.ru/logo.png', 'г. Москва, Чистопрудный бульвар, д. 12А', 2, POINT(37.6456, 55.7656), '{"url": "https://secondbreath.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Я свободен»', 'Помощь муниципальным и частным приютам для животных, организация стерилизации бездомных животных, поиск хозяев для животных из приютов.', 'https://yasvoboden.ru/logo.png', 'г. Москва, Волгоградский пр-кт, д. 3-5, стр. 2', 2, POINT(37.6789, 55.7342), '{"url": "https://yasvoboden.ru"}', '2024-01-20 12:00:00+03'),
('АНО «Центр социальных инициатив Добра и Успеха»', 'Реализация социальных проектов для поддержки социально незащищенных слоев населения, образовательные программы и культурные инициативы.', 'https://dobro-uspeh.ru/logo.png', 'г. Москва, ул. Кржижановского, д. 13, корп. 1', 2, POINT(37.5678, 55.6543), '{"url": "https://dobro-uspeh.ru"}', '2024-01-20 12:00:00+03'),
('Экологический центр «Сборка»', 'Пункт приема вторсырья, музей переработки, проведение экологических мероприятий и просветительских программ о раздельном сборе отходов.', 'https://eco-sborka.ru/logo.png', 'г. Москва, Чистопрудный бульвар, д. 14', 2, POINT(37.6445, 55.7647), '{"url": "https://eco-sborka.ru"}', '2024-01-20 12:00:00+03'),
('НКО «Кедровая тропа»', 'Экологические программы по увеличению популяции кедра в Москве, создание сети кедровых питомников, экскурсии по столичным лесам и паркам.', 'https://kedrtropa.ru/logo.png', 'г. Москва, Измайловский парк, аллея Большого Круга, д. 7', 2, POINT(37.7856, 55.7967), '{"url": "https://kedrtropa.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Живой»', 'Помощь взрослым с тяжелыми заболеваниями, сбор средств на приобретение лекарств и медицинского оборудования, оплата лечения и реабилитации.', 'https://livefund.ru/logo.png', 'г. Москва, ул. Остоженка, д. 3/14, оф. 26', 2, POINT(37.5943, 55.7456), '{"url": "https://livefund.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Антон тут рядом»', 'Помощь людям с аутизмом и другими ментальными особенностями, создание инклюзивной среды, обучение специалистов работе с особенными детьми.', 'https://antonryadom.ru/logo.png', 'г. Москва, ул. Большая Полянка, д. 21, стр. 4', 2, POINT(37.6234, 55.7389), '{"url": "https://antonryadom.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Банк еды «Русь»', 'Сбор продуктов питания от производителей и ретейлеров для передачи нуждающимся семьям, пожилым людям и бездомным через партнерские НКО.', 'https://foodbankrus.ru/logo.png', 'г. Москва, ул. Большая Декабрьская, д. 9', 2, POINT(37.6512, 55.7123), '{"url": "https://foodbankrus.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Обнаженные сердца»', 'Помощь детям с особенностями развития и их семьям, создание инклюзивных игровых площадок, обучение специалистов современным методам работы.', 'https://nakedheart.ru/logo.png', 'г. Москва, Старосадский пер., д. 5', 2, POINT(37.6389, 55.7634), '{"url": "https://nakedheart.ru"}', '2024-01-20 12:00:00+03'),
('Общественная организация «Зеленый патруль»', 'Общественный экологический контроль, мониторинг состояния окружающей среды, работа с властями по вопросам экологической безопасности регионов.', 'https://greenpatrol.ru/logo.png', 'г. Москва, ул. Новый Арбат, д. 11', 2, POINT(37.5934, 55.7534), '{"url": "https://greenpatrol.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Даунсайд Ап»', 'Помощь детям с синдромом Дауна и их семьям, ранняя помощь и психолого-педагогическое сопровождение, образовательные программы для специалистов.', 'https://downsideup.org/logo.png', 'г. Москва, Гагаринский пер., д. 4/2', 2, POINT(37.5823, 55.7289), '{"url": "https://downsideup.org"}', '2024-01-20 12:00:00+03'),
('РОО «Милосердие»', 'Помощь бездомным, малоимущим и одиноким пожилым людям: горячее питание, медицинская помощь, социальное сопровождение и реабилитация.', 'https://miloserdie.ru/logo.png', 'г. Москва, 3-й Монетчиковский пер., д. 10/1', 2, POINT(37.6456, 55.7123), '{"url": "https://miloserdie.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Память поколений»', 'Военно-патриотическое воспитание молодежи, сохранение исторической памяти о Великой Отечественной войне, помощь ветеранам и их семьям.', 'https://fondpg.ru/logo.png', 'г. Москва, Каширское ш., д. 3, к. 2', 2, POINT(37.6589, 55.6534), '{"url": "https://fondpg.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Линия жизни»', 'Помощь детям с тяжелыми заболеваниями сердечно-сосудистой и центральной нервной систем, сбор средств на сложные операции и лечение за рубежом.', 'https://life-line.ru/logo.png', 'г. Москва, ул. Флотская, д. 48А', 2, POINT(37.4856, 55.8123), '{"url": "https://life-line.ru"}', '2024-01-20 12:00:00+03'),
('Фонд «Солнечный город»', 'Помощь детям с онкологическими заболеваниями и их семьям, психологическая поддержка, организация реабилитации после лечения.', 'https://suncity-fund.ru/logo.png', 'г. Москва, Проспект Лихачева, д. 15', 2, POINT(37.5234, 55.8234), '{"url": "https://suncity-fund.ru"}', '2024-01-20 12:00:00+03'),
('НКО «Заповедное посольство»', 'Защита заповедников и национальных парков России, экологическое просвещение, лесовосстановительные работы и благоустройство памятников природы.', 'https://zapovedniki.ru/logo.png', 'г. Москва, ул. Крымский Вал, д. 9', 2, POINT(37.6045, 55.7356), '{"url": "https://zapovedniki.ru"}', '2024-01-20 12:00:00+03'),
('Проект «Город неравнодушных»', 'Волонтерские акции по благоустройству города, экологические инициативы, помощь социально незащищенным группам населения.', 'https://gorodneravnodushnyh.ru/logo.png', 'г. Москва, ул. Петровка, д. 26', 2, POINT(37.6123, 55.7645), '{"url": "https://gorodneravnodushnyh.ru"}', '2024-01-20 12:00:00+03'),
('Проект «Город неравнодушных»', 'Волонтерские акции по благоустройству города, экологические инициативы, помощь социально незащищенным группам населения.', 'https://gorodneravnodushnyh.ru/logo.png', 'г. Пермь, ул. Петровка, д. 26', 1, POINT(37.6123, 55.7645), '{"url": "https://gorodneravnodushnyh.ru"}', '2024-01-20 12:00:00+03');
